/*  css 样式初始化  */
*{box-sizing:border-box;}
*:before,*:after{box-sizing: border-box;}
*{margin: 0;padding: 0;}
ul,ol{ list-style: none;}

/*  新增图标的使用   */
.icon {
    width: 1em; height: 1em;
    vertical-align: -0.15em;
    fill: currentColor;
    overflow: hidden;
 }

/*   style 样式    */
body{background:#eee;}

.globalHeader{ 
    margin: 20px;
    display: flex;  
    justify-content: space-between;}
.globalHeader > input{
    width: 100%;
    margin-right: 10px;
    height: 35px;
    padding: 0 10px;  
    border:1px solid #ddd;  
    border-radius: 4px;
}
.globalHeader > button{
    white-space: nowrap;
    height: 23px;
    margin-top: 6px;
    padding: 0 10px;
    border:1px solid #29889D;
    background: #29889D;
    color: #fff;
    border-radius: 4px;
}

.globalMain{}

.siteList{
    display: flex;
    flex-wrap: wrap;
    margin: 30px;
}
.siteList > li{
    margin: 6px;
}
.siteList .site{
    width: 142px;
    padding: 20px 0;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    background: white;
    border:1px solid #ddd;
    border-radius: 4px;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}
.siteList .site > .logo{
    width: 64px;
    height: 64px;
    font-size: 32px;
    display: flex;
    justify-content: center;
    align-items: center;
    
}
.siteList .site > .link{
    font-size: 2px;
    margin-top: 4px;
}
.siteList .addButton{
    background: white;
    border:1px solid #ddd;
    width: 142px;
    padding: 20px 0;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}
.siteList .addButton .icon{
    width: 64px;
    height: 64px;  
}

.siteList .addButton .iconWrapper{
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
}
